// Αυτή η συνάρτηση εφαρμόζει τις μεταφράσεις στα στοιχεία της σελίδας
function applyLanguage(lang) {
  document.querySelectorAll('[data-lang-key]').forEach(el => {
    const key = el.getAttribute('data-lang-key');
    const translation = translations[lang][key];

    if (!translation) return;

    if (el.tagName === 'INPUT' || el.tagName === 'TEXTAREA') {
      el.placeholder = translation;
    } else {
      el.innerHTML = translation;
    }
  });
}

// ΝΕΑ ΣΥΝΑΡΤΗΣΗ: Ενημερώνει το cookie consent banner με τη νέα γλώσσα
function updateCookieConsentLanguage(lang) {
    // Ελέγχουμε αν η βιβλιοθήκη cookieconsent έχει φορτωθεί και είναι διαθέσιμη
    if (typeof window.cookieconsent === 'undefined' || typeof window.cookieconsent.initialise === 'undefined') {
        console.warn("Cookie consent library not loaded or not fully initialized, cannot update language.");
        return;
    }

    const langContent = translations[lang];

    if (!langContent) {
        console.error(`Translations for language "${lang}" not found.`);
        return;
    }

    // ΠΡΟΣΘΗΚΗ: Ρητή αφαίρεση ΟΛΩΝ των στοιχείων του banner από το DOM
    const cookieBanners = document.querySelectorAll('.cc-window');
    cookieBanners.forEach(banner => {
        banner.remove();
        console.log('Existing cookie consent banner element explicitly removed from DOM.');
    });

    // Αφαίρεση τυχόν κουμπιών ανάκλησης
    const revokeButtons = document.querySelectorAll('.cc-revoke');
    revokeButtons.forEach(button => {
        button.remove();
        console.log('Existing cookie consent revoke button explicitly removed from DOM.');
    });

    // Καταστρέφουμε την υπάρχουσα βιβλιοθήκη αν υπάρχει (καλή πρακτική)
    if (window.cookieconsent.getCookieConsentInstance) {
        const instance = window.cookieconsent.getCookieConsentInstance();
        if (instance && typeof instance.destroy === 'function') {
            instance.destroy(); // Καταστρέφει το υπάρχον banner
            console.log('Existing cookie consent banner instance destroyed via method.');
        }
    }

    // Και τώρα το επανα-αρχικοποιούμε με τις νέες μεταφράσεις
    window.cookieconsent.initialise({
        "palette": {
            "popup": { "background": "#2C3E50", "text": "#FFFFFF" },
            "button": { "background": "#F4D35E", "text": "#2C3E50" }
        },
        "theme": "base",
        "content": {
            "message": langContent["cookie-message"],
            "dismiss": langContent["cookie-accept"],
            "deny": langContent["cookie-deny"],
            "link": langContent["cookie-learn-more"],
            "href": langContent["cookie-privacy-policy-url"]
        },
        "type": "opt-in",
        "revokable": false, // <-- ΑΛΛΑΞΕ ΑΥΤΟ ΤΟ TRUE ΣΕ FALSE
        "position": "bottom",
        "onStatusChange": function(status, chosenBefore) {
          console.log('Cookie consent status changed:', status, 'chosenBefore:', chosenBefore);
        },
        "onInitialise": function(status) {
          console.log('Cookie consent banner re-initialized with status: ', status);
        }
    });
}


function setLanguage(lang) {
  localStorage.setItem("language", lang); // Αποθήκευση γλώσσας
  applyLanguage(lang); // Εφαρμογή γλώσσας στην υπόλοιπη σελίδα
  updateCookieConsentLanguage(lang); // Εφαρμογή γλώσσας στο cookie banner
}

// Χειρισμός εμφάνισης του menu
function toggleMenu() {
  const nav = document.getElementById("main-nav");
  nav.classList.toggle("open");
}

document.documentElement.classList.add("lang-loading");

document.addEventListener("DOMContentLoaded", () => {
    // Εφαρμόζουμε τη γλώσσα μόλις φορτώσει το DOM, καλώντας την setLanguage
    const savedLang = localStorage.getItem("language") || "el";
    document.documentElement.lang = savedLang;
    document.documentElement.setAttribute("data-language", savedLang);
    setLanguage(savedLang); // Καλεί την setLanguage για να αρχικοποιήσει και το banner των cookies
    document.documentElement.classList.remove("lang-loading");
});
